const bookReducer = (state = [], action) => {
    switch(action.type){
        case 'ADD_BOOK':
            return state.concat([action.data]);
        case 'DELETE_BOOK':
            return state.filter((book) => book.id !== action.id);
        case 'EDIT_BOOK' : 
            return state.map((book) => book.id === action.id ? {...book, EditBook: !book.EditBook} : book)
        default:
            return state;
    }
}


export default bookReducer;
