import React, { Component } from "react";
import { connect } from "react-redux";

class Book extends Component {
  render() {
    return (
      <div className="mb-3">
        <div class="list-group">
          <div
            href="#"
            class="list-group-item list-group-item-action flex-column align-items-start"
          >

              <p class="mb-1">ชื่อหนังสือ : {this.props.book.BookName}</p>

              <p class="mb-1">วันที่เริ่มอ่าน : {this.props.book.Date_readStart}</p>
              <p class="mb-1">วันที่อ่านจบ : {this.props.book.Date_readEnd}</p>
              <p class="mb-1">ชื่อคนเขียน : {this.props.book.Author_name}</p>

            <button
              type="button"
              class="btn btn-secondary pr-2"
              onClick={() =>
                this.props.dispatch({
                  type: "EDIT_BOOK",
                  id: this.props.book.id,
                })
              }
            >
              Edit
            </button>
            <button
              type="button"
              class="btn btn-danger ml-5"
              onClick={() =>
                this.props.dispatch({
                  type: "DELETE_BOOK",
                  id: this.props.book.id,
                })
              }
            >
              Delete
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default connect()(Book);
