import React, { Component } from "react";
import { connect } from "react-redux";

class Book extends Component {
  render() {
    return (
      <div className="mb-3">
        <div class="list-group">
          <div
            href="#"
            class="list-group-item list-group-item-action flex-column align-items-start"
          >
            <div className="row">
              <div className="col-2 text-end pr-0"><h5>ชื่อหนังสือ :</h5></div>
              <div className="col-10"><h5>{this.props.book.BookName}</h5></div>
            </div>



              <h5 class="mb-1">วันที่เริ่มอ่าน : {this.props.book.Date_readStart}</h5>
              <h5 class="mb-1">วันที่อ่านจบ : {this.props.book.Date_readEnd}</h5>
              <h5 class="mb-1">ชื่อคนเขียน : {this.props.book.Author_name}</h5>

            <button
              type="button"
              class="btn btn-secondary pr-2"
              onClick={() =>
                this.props.dispatch({
                  type: "EDIT_BOOK",
                  id: this.props.book.id,
                })
              }
            >
              Edit
            </button>
            <button
              type="button"
              class="btn btn-danger" style={{margin:10}}
              onClick={() =>
                this.props.dispatch({
                  type: "DELETE_BOOK",
                  id: this.props.book.id,
                })
              }
            >
              Delete
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default connect()(Book);
