import React, { Component } from "react";
import { connect } from "react-redux";

class Book extends Component {
  render() {
    return (
      <div>
        <h1>{this.props.book.BookName}</h1>
        <button type="button" class="btn btn-secondary" onClick={() => this.props.dispatch({type: ''})}>
          Edit
        </button>
        <button
          type="button"
          class="btn btn-danger"
          onClick={() =>
            this.props.dispatch({ type: "DELETE_BOOK", id: this.props.book.id })
          }
        >
          Delete
        </button>
      </div>
    );
  }
}

export default connect()(Book);
