import React, { Component } from "react";
import { connect } from "react-redux";

class Book extends Component {
  render() {
    return (
      <div>
        <div class="list-group">
          <div
            href="#"
            class="list-group-item list-group-item-action flex-column align-items-start"
          >
            <div class="d-flex w-100 justify-content-between">
              <h5 class="mb-1">ชื่อหนังสือ : {this.props.book.BookName}</h5>
            </div>
              <h5 class="mb-1">วันที่เริ่มอ่าน : {this.props.book.Date_readStart}</h5>
              <h5 class="mb-1">วันที่อ่านจบ : {this.props.book.Date_readEnd}</h5>
              <h5 class="mb-1">ชื่อคนเขียน : {this.props.book.Author_name}</h5>

            <button
              type="button"
              class="btn btn-secondary"
              onClick={() =>
                this.props.dispatch({
                  type: "EDIT_BOOK",
                  id: this.props.book.id,
                })
              }
            >
              Edit
            </button>
            <button
              type="button"
              class="btn btn-danger"
              onClick={() =>
                this.props.dispatch({
                  type: "DELETE_BOOK",
                  id: this.props.book.id,
                })
              }
            >
              Delete
            </button>
          </div>
        </div>

        <p>{this.props.book.BookName}</p>
        <p>{this.props.book.Date_readStart}</p>
        <p>{this.props.book.Date_readEnd}</p>
        <p>{this.props.book.Author_name}</p>
      </div>
    );
  }
}

export default connect()(Book);
